package practice;

public class triangleexercise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		for (int i = 0;i<4 ; i++ )
		{
			int e =1;
			for (int j = 0;j<=i ; j++ )
			{
				
				System.out.print(e);
				System.out.print("\t");
				e++;
			}
			
			System.out.println();

		}

	}

}
